document.addEventListener('DOMContentLoaded',()=>{const menuBtn=document.getElementById('menu-btn');const navMenu=document.getElementById('nav-menu');menuBtn.addEventListener('click',()=>{navMenu.classList.toggle('translate-y-full');navMenu.classList.toggle('opacity-0');});const navLinks=document.querySelectorAll('.nav-link');navLinks.forEach(link=>{link.addEventListener('click',()=>{if(window.innerWidth<1024){navMenu.classList.add('translate-y-full');navMenu.classList.add('opacity-0');}});});window.addEventListener('resize',()=>{if(window.innerWidth>=1024){navMenu.classList.remove('translate-y-full');navMenu.classList.remove('opacity-0');}else{navMenu.classList.add('translate-y-full');navMenu.classList.add('opacity-0');}});});;document.addEventListener('DOMContentLoaded',function(){const aliadosContainer=document.querySelector('#aliados-container');const links=aliadosContainer.querySelectorAll('#aliados-link');let currentIndex=0;let intervalId;function showLogo(index){links.forEach(link=>{link.style.transform=`translateX(-${index * 100}%)`;link.style.transition='transform 0.5s ease-in-out';});}
function rotateLogos(){currentIndex=(currentIndex+1)%links.length;showLogo(currentIndex);}
function startAutoRotation(){intervalId=setInterval(rotateLogos,3000);}
function stopAutoRotation(){clearInterval(intervalId);}
showLogo(currentIndex);startAutoRotation();aliadosContainer.addEventListener('mouseenter',stopAutoRotation);aliadosContainer.addEventListener('mouseleave',startAutoRotation);});;